// DETALLES DE LA CARTA
import { useParams } from "react-router-dom";
import { useState, } from "react";
import { useEffect } from "react";

import style from "../views/Detail.module.css";




const Detail = () => {
  const { detailId } = useParams();

  const [character, setCharacter] = useState({});

  useEffect(() => {
  const URL = "https://be-a-rym.up.railway.app/api";
  const key = "3ecf99bb3382.b75baf9da14b9a8b40d6";

    fetch(`${URL}/character/${detailId }?key=${key}`)
      .then((response) => response.json())
      .then((char) => {
        if (char.name) {
          setCharacter(char);
        } else {
          window.alert("No hay personajes con ese ID");
        }
      })
      .catch((err) => {
        window.alert("No hay personajes con ese ID");
      });
    return setCharacter({});
  }, [detailId ]);

  return (
    <div>
    
      {character.name ? (
        <div className={style.cardDF} >
          <h2>{character.name}</h2>
          <p>{character.status}</p>
          <p>{character.species}</p>
          <p>{character.gender}</p>
          <p>{character.origin?.name}</p>
          
          <img className={style.cardimg} src={character.image} alt="imagen" />
        
        </div>
      ) : (
        <h3>Loading...</h3>
      )}
    </div>
  );
};

export default Detail;
