import { ADD_PERS, FILTER, REMOVE_PERS } from "./actions";

const initialState = {
  myFavorites: [],
  allCharacters: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_PERS:
      return {
        ...state,
        // indicaciones
        myFavorite: [...state.allCharacters, action.payload],
        allCharacters: [...state, action.payload],
      };

    case REMOVE_PERS:
      return {
        ...state,
        // indicaciones
        myFavorites: state.myFavorites.filter(
          (pers) => pers.id !== action.payload
        ),
      };

      case FILTER:
        return {...state,
          allCharacters: [...state.allCharacters.filter]
        };
    default:
      return { ...state };
  }
};

export default rootReducer;
