import { Link } from "react-router-dom";
import styled from "./Card.module.css";

import { agregarPersFav, removePersFav } from "../../redux/actions";
import { connect } from "react-redux";
import { useState } from "react";

//? ------------------------------------------

function Card({
  id,
  name,
  species,
  gender,
  image,
  onClose,
  removePersFav,
  agregarPersFav,
  myFavorites
}) {
  const [isFav, setIsFav] = useState(false);

  const handleFavorite = () => {
    if (isFav) {
      setIsFav(false);
      removePersFav(id);
    } else {
      setIsFav(true);
      agregarPersFav(id, name, species, gender, image, onClose);
    }
  };

  useEffect(() => {
    myFavorites.forEach((fav) => {
       if (fav.id === props.id) {
          setIsFav(true);
       }
    });
 }, [myFavorites]);

  return (
    <div className={styled.card}>
      <div className={styled.fondoX}>
        {isFav ? (
          <button onClick={handleFavorite}>‚ù§Ô∏è</button>
        ) : (
          <button onClick={handleFavorite}>ü§ç</button>
        )}

        <button onClick={() => onClose(id)} className={styled.botonx}>
          x
        </button>
      </div>
      <div>
        <button className={styled.fondoX}>Fav</button>
      </div>

      <Link to={`/detail/${id}`}>
        <h2 className={styled.h2Name}>{name}</h2>
      </Link>

      <img src={image} alt="" />
      <h3 className={styled.species}>Species: {species}</h3>
      <h3 className={styled.gender}>Gende: {gender}</h3>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    agregarPersFav: (personaje) => {
      dispatch(agregarPersFav(personaje));
    },
    removePersFav: (id) => {
      dispatch(removePersFav(id));
    },
  };
};

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorite,
  };
};

export default connect(mapDispatchToProps, mapStateToProps)(Card);
